const t=JSON.parse('{"key":"v-46ad6f5e","path":"/docs/%E4%B8%AD%E5%8C%BB%E5%AD%A6%E4%B9%A0/%E4%B8%AD%E5%8C%BB%E5%9F%BA%E7%A1%80/%E4%BA%94%E8%84%8F/%E5%BF%83%E5%8C%85.html","title":"心包","lang":"zh-CN","frontmatter":{"date":"2023-05-29T00:00:00.000Z","category":["学习"],"tag":["中医基础"],"title":"心包","description":"心包络，简称心包，亦称“膻中”，是包在心脏外面的包膜，具有保护心脏的作用。心包络既是心的外围，故邪气犯心，常先侵犯心包络。心都和中枢神经的活动有关，如果外邪侵犯心脏，首先是心包受到影响，如临床上急性传染病等因高热引起的神昏谵语，发狂，称之为“热入心包“，在治疗上就是以“清心”为主，说明心包和心从辨证的角度来看是一致的，只不过反映病情的浅、深、轻、重程度的不同而已。是心脏外面的包膜，有保护心脏的作用，在经络学说中，手厥阴心包经与手少阳三焦经相为表里，故心包络属于脏。 心包的结构 心包是包心脏外面的一层薄膜，心包和心脏壁的中间有浆液，能润滑心肌，使心脏活动时不跟胸腔摩擦而受伤。可分为浆膜心包和纤维心包。","head":[["meta",{"property":"og:url","content":"https://en0x.com/docs/%E4%B8%AD%E5%8C%BB%E5%AD%A6%E4%B9%A0/%E4%B8%AD%E5%8C%BB%E5%9F%BA%E7%A1%80/%E4%BA%94%E8%84%8F/%E5%BF%83%E5%8C%85.html"}],["meta",{"property":"og:site_name","content":"EN0X"}],["meta",{"property":"og:title","content":"心包"}],["meta",{"property":"og:description","content":"心包络，简称心包，亦称“膻中”，是包在心脏外面的包膜，具有保护心脏的作用。心包络既是心的外围，故邪气犯心，常先侵犯心包络。心都和中枢神经的活动有关，如果外邪侵犯心脏，首先是心包受到影响，如临床上急性传染病等因高热引起的神昏谵语，发狂，称之为“热入心包“，在治疗上就是以“清心”为主，说明心包和心从辨证的角度来看是一致的，只不过反映病情的浅、深、轻、重程度的不同而已。是心脏外面的包膜，有保护心脏的作用，在经络学说中，手厥阴心包经与手少阳三焦经相为表里，故心包络属于脏。 心包的结构 心包是包心脏外面的一层薄膜，心包和心脏壁的中间有浆液，能润滑心肌，使心脏活动时不跟胸腔摩擦而受伤。可分为浆膜心包和纤维心包。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-06-05T07:43:22.000Z"}],["meta",{"property":"article:author","content":"Wesley"}],["meta",{"property":"article:tag","content":"中医基础"}],["meta",{"property":"article:published_time","content":"2023-05-29T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2023-06-05T07:43:22.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"心包\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2023-05-29T00:00:00.000Z\\",\\"dateModified\\":\\"2023-06-05T07:43:22.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Wesley\\",\\"url\\":\\"https://en0x.com/\\"}]}"]]},"headers":[],"git":{"createdTime":1685951002000,"updatedTime":1685951002000,"contributors":[{"name":"Wesley","email":"wesleyrowe@proton.me","commits":1}]},"readingTime":{"minutes":3.47,"words":1041},"filePathRelative":"docs/中医学习/中医基础/五脏/心包.md","localizedDate":"2023年5月29日","excerpt":"<p><strong>心包络，简称心包</strong>，亦称“膻中”，是包在心脏外面的包膜，具有保护心脏的作用。心包络既是心的外围，故邪气犯心，常先侵犯心包络。心都和中枢神经的活动有关，如果外邪侵犯心脏，首先是心包受到影响，如临床上急性传染病等因高热引起的神昏谵语，发狂，称之为“热入心包“，在治疗上就是以“清心”为主，说明心包和心从辨证的角度来看是一致的，只不过反映病情的浅、深、轻、重程度的不同而已。是心脏外面的包膜，有保护心脏的作用，在经络学说中，手厥阴心包经与手少阳三焦经相为表里，故心包络属于脏。</p>\\n<ul>\\n<li>心包的结构</li>\\n</ul>\\n<p>心包是包心脏外面的一层薄膜，心包和心脏壁的中间有浆液，能润滑心肌，使心脏活动时不跟胸腔摩擦而受伤。可分为浆膜心包和纤维心包。</p>","autoDesc":true}');export{t as data};
