const e=JSON.parse('{"key":"v-bbb69f9a","path":"/docs/%E4%B8%AD%E5%8C%BB%E5%AD%A6%E4%B9%A0/%E4%B8%AD%E5%8C%BB%E5%9F%BA%E7%A1%80/%E7%97%85%E6%9C%BA/2%E3%80%81%E5%86%85%E7%94%9F%E4%BA%94%E6%B0%94/%E6%B4%A5%E4%BC%A4%E5%8C%96%E7%87%A5.html","title":"津伤化燥","lang":"zh-CN","frontmatter":{"date":"2023-05-29T00:00:00.000Z","title":"津伤化燥","category":["学习"],"tag":["中医基础"],"description":"内燥的含义 津伤化燥又称“内燥”。内燥。，是指机体津液不足，人体各组织器官和孔窍失其濡润，因而出现以干燥枯涩失润为特征的病理变化，故又称津伤化燥。内燥多因久病伤阴耗液或大污、大吐、大下，或亡血失精导致阴亏液少，以及某些热性病过程中的热邪伤阴或湿邪化燥等所致。由于津液亏少，不足以内溉脏腑、外润腠理孔窍，从而燥热便由内而生，故临床多见干燥不润等病变。 一般来说，阴津亏损，可产生内燥，而实热伤津亦可导致燥热内生。内燥病变可发生于各脏腑组织，以肺、胃、肾及大肠为多见。因为肺为燥金之脏，主气，司全身精血津液的敷布。肺气虚弱，则水精不能四布而化燥，其病属虚。大肠为燥金之腑，主津，故肠胃燥热，灼伤津液，亦常致燥，多属于实。此外，肾总司一身的气化活动，若肾的气化失常，津液不布，也可以导致内燥：故内燥起于肺、胃，肾，胃为重，肾尤为重。","head":[["meta",{"property":"og:url","content":"https://en0x.com/docs/%E4%B8%AD%E5%8C%BB%E5%AD%A6%E4%B9%A0/%E4%B8%AD%E5%8C%BB%E5%9F%BA%E7%A1%80/%E7%97%85%E6%9C%BA/2%E3%80%81%E5%86%85%E7%94%9F%E4%BA%94%E6%B0%94/%E6%B4%A5%E4%BC%A4%E5%8C%96%E7%87%A5.html"}],["meta",{"property":"og:site_name","content":"EN0X"}],["meta",{"property":"og:title","content":"津伤化燥"}],["meta",{"property":"og:description","content":"内燥的含义 津伤化燥又称“内燥”。内燥。，是指机体津液不足，人体各组织器官和孔窍失其濡润，因而出现以干燥枯涩失润为特征的病理变化，故又称津伤化燥。内燥多因久病伤阴耗液或大污、大吐、大下，或亡血失精导致阴亏液少，以及某些热性病过程中的热邪伤阴或湿邪化燥等所致。由于津液亏少，不足以内溉脏腑、外润腠理孔窍，从而燥热便由内而生，故临床多见干燥不润等病变。 一般来说，阴津亏损，可产生内燥，而实热伤津亦可导致燥热内生。内燥病变可发生于各脏腑组织，以肺、胃、肾及大肠为多见。因为肺为燥金之脏，主气，司全身精血津液的敷布。肺气虚弱，则水精不能四布而化燥，其病属虚。大肠为燥金之腑，主津，故肠胃燥热，灼伤津液，亦常致燥，多属于实。此外，肾总司一身的气化活动，若肾的气化失常，津液不布，也可以导致内燥：故内燥起于肺、胃，肾，胃为重，肾尤为重。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-06-05T07:43:22.000Z"}],["meta",{"property":"article:author","content":"Wesley"}],["meta",{"property":"article:tag","content":"中医基础"}],["meta",{"property":"article:published_time","content":"2023-05-29T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2023-06-05T07:43:22.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"津伤化燥\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2023-05-29T00:00:00.000Z\\",\\"dateModified\\":\\"2023-06-05T07:43:22.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Wesley\\",\\"url\\":\\"https://en0x.com/\\"}]}"]]},"headers":[{"level":2,"title":"内燥的含义","slug":"内燥的含义","link":"#内燥的含义","children":[]},{"level":2,"title":"内燥的病理变化","slug":"内燥的病理变化","link":"#内燥的病理变化","children":[]},{"level":2,"title":"外燥与内燥的关系","slug":"外燥与内燥的关系","link":"#外燥与内燥的关系","children":[]}],"git":{"createdTime":1685951002000,"updatedTime":1685951002000,"contributors":[{"name":"Wesley","email":"wesleyrowe@proton.me","commits":1}]},"readingTime":{"minutes":2.24,"words":673},"filePathRelative":"docs/中医学习/中医基础/病机/2、内生五气/津伤化燥.md","localizedDate":"2023年5月29日","excerpt":"<h2> 内燥的含义</h2>\\n<p>津伤化燥又称“内燥”。内燥。，是指机体津液不足，人体各组织器官和孔窍失其濡润，因而出现以干燥枯涩失润为特征的病理变化，故又称津伤化燥。内燥多因久病伤阴耗液或大污、大吐、大下，或亡血失精导致阴亏液少，以及某些热性病过程中的热邪伤阴或湿邪化燥等所致。由于津液亏少，不足以内溉脏腑、外润腠理孔窍，从而燥热便由内而生，故临床多见干燥不润等病变。</p>\\n<p>一般来说，阴津亏损，可产生内燥，而实热伤津亦可导致燥热内生。内燥病变可发生于各脏腑组织，以肺、胃、肾及大肠为多见。因为肺为燥金之脏，主气，司全身精血津液的敷布。肺气虚弱，则水精不能四布而化燥，其病属虚。大肠为燥金之腑，主津，故肠胃燥热，灼伤津液，亦常致燥，多属于实。此外，肾总司一身的气化活动，若肾的气化失常，津液不布，也可以导致内燥：故内燥起于肺、胃，肾，胃为重，肾尤为重。</p>","autoDesc":true}');export{e as data};
