import{_ as t,X as _,Y as n,Z as l}from"./framework-696d86b8.js";const e={},o=l("ul",null,[l("li",null,"温毒，病名。见《肘后备急方》卷二。")],-1),s=l("p",null,"①指春温发斑。《医学入门》卷三：“春温发斑，谓之温毒发斑。”参见温毒发斑条。",-1),c=l("p",null,"②指温邪热毒病证之概称。《温病条辨.上焦篇》：“温毒者，诸温挟毒，秽浊太甚也。”临床以突发寒战高热，头痛恶心，身骨酸痛，心烦，口渴，溺黄赤，脉洪数（或数大）苔黄、舌质红绛为常见，其后或见头面或颐部红肿，或咽喉肿痛起腐，或身发斑疹等。其治以清热解毒为大法。选用普济消毒饮、升麻葛根汤、三黄石膏汤、黄连解毒汤等方加减，并可配合针刺及外治法。热入营血者，宜凉营解毒，用清瘟败毒散加减。参见大头瘟、发颐、烂喉痧等条。可见于流行性腮腺炎、头面丹毒、猩红热及重症麻疹等病。",-1),u=l("p",null,"③指赤癍、丹疹类疾病。吴坤安谓：“秋应凉而反热，冬应寒而反温，或天时亢旱久燥，温疫流行，发为赤癍、丹疹，其毒弥满三焦，目赤舌绛，汗出津津。……宜凉膈散去芒硝、大黄，加石膏、牛蒡、人中黄，大便秘者，去硝、留黄。”（《伤寒指掌》卷三）。",-1),i=l("ul",null,[l("li",null,"温毒")],-1),d=l("p",null,"指感受温热时毒而发生的急性感染，即所谓“诸温夹毒”。临床以高热、头面或咽喉肿痛、出血性斑疹为特征。",-1),h=l("p",null,"大抵包括二类疾病：一是头面、口腔、咽喉感染的化脓性疾患，如继发性化脓性腮腺炎（发颐）、扁桃体周围脓肿、急性化脓性扁桃体炎等；一是急性流行性传染病，如流行性腮腺炎（痄腮）、猩红热、斑疹伤寒等。",-1),r=l("p",null,"名词解释为：温毒是指感受温毒病邪所引起的具有肿毒特征的一类温病。",-1),a=[o,s,c,u,i,d,h,r];function p(f,m){return _(),n("div",null,a)}const B=t(e,[["render",p],["__file","温邪.html.vue"]]);export{B as default};
