const t=JSON.parse('{"key":"v-d96cf05c","path":"/docs/%E4%B8%AD%E5%8C%BB%E5%AD%A6%E4%B9%A0/%E4%B8%AD%E5%8C%BB%E5%9F%BA%E7%A1%80/%E7%97%85%E5%9B%A0/%E7%97%85%E5%9B%A0.html","title":"病因","lang":"zh-CN","frontmatter":{"date":"2023-05-29T00:00:00.000Z","title":"病因","category":["学习"],"tag":["中医基础"],"description":"五轮八廓(Five Rings and Eight Octants)，中国古代医家阐述眼与脏腑相互关系，并指导诊治眼病的两种学说。分别由五行、八卦说衍化而来。五轮指风轮、气轮、肉轮、血轮、水轮，是将眼划分为五个部位，分属于不同的脏腑，从而把眼局部与脏腑统一成为一个整体，用以说明眼的生理、病理现象，指导眼病的辨证论治。八廓是将白睛按八卦的部位划出八个不同的方位，而后各隶属于六腑、心包和命门。当眼睛发病时，可通过观察白睛呈现的血脉丝络的方位及其色泽、粗细、多寡等，为眼病的辨证论治提供依据。五轮与八廓既有区别又有联系，故一般通称为五轮八廓。这两种学说在过去对中医眼科曾起过积极的作用，随着时代的推移，五轮在临床上尚有某种参考意义，而八廓则因历代医家在部位划分、脏腑分属上极不统一，而且相互矛盾，很难指导临床，故已很少应用。","head":[["meta",{"property":"og:url","content":"https://en0x.com/docs/%E4%B8%AD%E5%8C%BB%E5%AD%A6%E4%B9%A0/%E4%B8%AD%E5%8C%BB%E5%9F%BA%E7%A1%80/%E7%97%85%E5%9B%A0/%E7%97%85%E5%9B%A0.html"}],["meta",{"property":"og:site_name","content":"EN0X"}],["meta",{"property":"og:title","content":"病因"}],["meta",{"property":"og:description","content":"五轮八廓(Five Rings and Eight Octants)，中国古代医家阐述眼与脏腑相互关系，并指导诊治眼病的两种学说。分别由五行、八卦说衍化而来。五轮指风轮、气轮、肉轮、血轮、水轮，是将眼划分为五个部位，分属于不同的脏腑，从而把眼局部与脏腑统一成为一个整体，用以说明眼的生理、病理现象，指导眼病的辨证论治。八廓是将白睛按八卦的部位划出八个不同的方位，而后各隶属于六腑、心包和命门。当眼睛发病时，可通过观察白睛呈现的血脉丝络的方位及其色泽、粗细、多寡等，为眼病的辨证论治提供依据。五轮与八廓既有区别又有联系，故一般通称为五轮八廓。这两种学说在过去对中医眼科曾起过积极的作用，随着时代的推移，五轮在临床上尚有某种参考意义，而八廓则因历代医家在部位划分、脏腑分属上极不统一，而且相互矛盾，很难指导临床，故已很少应用。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-06-05T07:43:22.000Z"}],["meta",{"property":"article:author","content":"Wesley"}],["meta",{"property":"article:tag","content":"中医基础"}],["meta",{"property":"article:published_time","content":"2023-05-29T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2023-06-05T07:43:22.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"病因\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2023-05-29T00:00:00.000Z\\",\\"dateModified\\":\\"2023-06-05T07:43:22.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Wesley\\",\\"url\\":\\"https://en0x.com/\\"}]}"]]},"headers":[],"git":{"createdTime":1685951002000,"updatedTime":1685951002000,"contributors":[{"name":"Wesley","email":"wesleyrowe@proton.me","commits":1}]},"readingTime":{"minutes":3.15,"words":945},"filePathRelative":"docs/中医学习/中医基础/病因/病因.md","localizedDate":"2023年5月29日","excerpt":"<p><strong>五轮八廓(Five Rings and Eight Octants)</strong>，中国古代医家阐述眼与脏腑相互关系，并指导诊治眼病的两种学说。分别由五行、八卦说衍化而来。五轮指风轮、气轮、肉轮、血轮、水轮，是将眼划分为五个部位，分属于不同的脏腑，从而把眼局部与脏腑统一成为一个整体，用以说明眼的生理、病理现象，指导眼病的辨证论治。八廓是将白睛按八卦的部位划出八个不同的方位，而后各隶属于六腑、心包和命门。当眼睛发病时，可通过观察白睛呈现的血脉丝络的方位及其色泽、粗细、多寡等，为眼病的辨证论治提供依据。五轮与八廓既有区别又有联系，故一般通称为五轮八廓。这两种学说在过去对中医眼科曾起过积极的作用，随着时代的推移，五轮在临床上尚有某种参考意义，而八廓则因历代医家在部位划分、脏腑分属上极不统一，而且相互矛盾，很难指导临床，故已很少应用。</p>","autoDesc":true}');export{t as data};
