import{_ as t,X as o,Y as s,Z as e,$ as _}from"./framework-696d86b8.js";const n={},c=e("p",null,[e("strong",null,"肾"),_("，①人体器官名。系五脏之一。与膀胱相表里。主藏精，藏先天之精和后天之精，主发育、生长、生殖。《素问.六节脏象论》：“肾者主蛰，封藏之本，精之处也。”《素问.上古天真论》：“肾者主水，受五脏六腑之精而藏之。”又称之为后天之本。肾为水之下源，主水，与肺、脾、三焦、膀胱等脏腑共同调节水液代谢，是水液代谢的重要脏器。《素问.逆调论》：“肾者水脏，主津液。”肾脉上连肺，主纳气。《难经.四难》：“呼出心与肺，吸入肾与肝。”又主骨，生髓，通于脑，开窍于耳及二阴，外应于腰。《素问.痿论》：“肾主身之骨髓。”《灵枢.脉度》：“肾气通于耳，肾和则能闻五音矣。”《素问.脉要精微论》：“腰者，肾之府。”因开窍二阴而司大小便。又寄藏命门之火，为元阴、元阳之脏，故有“水火之脏”、“阴阳之宅”之称。")],-1),l=e("p",null,"②脉学名词。指足少阴肾经。《灵枢.本输》：“肾出涌泉……足少阴经也。”",-1),r=e("p",null,"③脉学名词。指肾之真脏脉。《素问.平人气象论》：“肾见戊已死，是谓真脏见皆死。”",-1),a=e("p",null,"④推拿部位名。见肾经条。",-1),d=[c,l,r,a];function i(u,h){return o(),s("div",null,d)}const f=t(n,[["render",i],["__file","肾.html.vue"]]);export{f as default};
